void wdtoff()
{
  cli();
  wdr();
  MCUSR &= ~(1 << WDRF);
  WDTCSR |= (1 << WDCE) | (1 << WDE);
  WDTCSR = 0x00;
  sei();
}

void swdtxf() //0.032s (~35 millis)
{
  cli();
  wdr();
  MCUSR &= ~(1 << WDRF);
  WDTCSR |= (1 << WDCE) | (1 << WDE);
  WDTCSR =  (1 << WDP0) | (1 << WDIE);
  wdtt = wdtxft;
  sei();
}

void swdtf() //0.25s (~274 millis)
{
  cli();
  wdr();
  MCUSR &= ~(1 << WDRF);
  WDTCSR |= (1 << WDCE) | (1 << WDE);
  WDTCSR =  (1 << WDP2) | (1 << WDIE);
  wdtt = wdtft;
  sei();
}

void swdtn() //0.5 (~549 millis)
{
  cli();
  wdr();
  MCUSR &= ~(1 << WDRF);
  WDTCSR |= (1 << WDCE) | (1 << WDE);
  WDTCSR =  (1 << WDP2) | (1 << WDP0) | (1 << WDIE);
  wdtt = wdtnt;
  sei();
}

void swdts() //2s (~2196)
{
  cli();
  wdr();
  MCUSR &= ~(1 << WDRF);
  WDTCSR |= (1 << WDCE) | (1 << WDE);
  WDTCSR =  (1 << WDP2) | (1 << WDP1) | (1 << WDP0) | (1 << WDIE);
  wdtt = wdtst;
  sei();
}

void swdtxs() //8s (~8781)
{
  cli();
  wdr();
  MCUSR &= ~(1 << WDRF);
  WDTCSR |= (1 << WDCE) | (1 << WDE);
  WDTCSR =  (1 << WDP3) | (1 << WDP0) | (1 << WDIE);
  wdtt = wdtxst;
  sei();
}

void calwdt()
{
  swdtxf();
  ms = 0;
  wdtf = 0;
  while ( wdtf == 0);
  wdtxft = ms;
  swdtf();
  ms = 0;
  wdtf = 0;
  while ( wdtf == 0);
  wdtft = ms;
  swdtn();
  ms = 0;
  wdtf = 0;
  while ( wdtf == 0);
  wdtnt = ms;
  swdts();
  ms = 0;
  wdtf = 0;
  while ( wdtf == 0);
  wdtst = ms;
  swdtxs();
  ms = 0;
  wdtf = 0;
  while ( wdtf == 0);
  wdtxst = ms;
  wdtoff();
}


