void glcdline(byte line)
{
  if (line < 0) {
    line = 0;
  }
  if (line > 8) {
    line = 8;
  }
  line = 176 + line;
  glcdcommand(line);  //set line
}

void glcdcolumn(byte payload)
{
  if (payload < 0) {
    payload = 0;
  }
  if (payload > 95) {
    payload = 95;
  }

  byte lower = 0x0F;
  byte upper = 0x70;

  lower = payload & 0x0F;
  upper = payload & 0x70;

  upper = upper >> 4;

  upper = 0x10 | upper;

  glcdcommand(upper);
  glcdcommand(lower);
}

void glcdpayload(byte payload)
{
  for (int i = 7; i >= 0; i--)
  {
    if ((payload >> i) & 1) sdaon();
    else sdaoff();
    //toggle clock
    sckon();
    sckoff();
  }
}

void glcdcommand(byte payload)
{
  csoff();//digitalWrite(_CSPin, LOW);
  sdaoff();//digitalWrite(_SDAPin, LOW);
  sckon(); //digitalWrite(_SCKPin, HIGH);
  sckoff(); //digitalWrite(_SCKPin, LOW);
  glcdpayload(payload);
  cson();//digitalWrite(_CSPin, HIGH);
}

void glcddata(byte payload)
{
  csoff();//digitalWrite(_CSPin, LOW);
  sdaon();//digitalWrite(_SDAPin, HIGH);
  sckon(); //digitalWrite(_SCKPin, HIGH);
  sckoff(); //digitalWrite(_SCKPin, LOW);
  glcdpayload(payload);
  cson();//digitalWrite(_CSPin, HIGH);
}

