void glcdcharacter(char character)
{
  int starti = (character - 0x20) * 5;
  int stopi = ((character - 0x20) + 1) * 5;
  for (int index = starti; index < stopi; index++)
  {
    glcddata(pgm(&(ASCII[index])));
  }
  glcddata(0x00);
}

