void glcdalt(int val)
{
  uint8_t dig1 = 0;
  uint8_t dig2 = 0;
  uint8_t dig3 = 0;
  uint8_t dig4 = 0;
  uint8_t minus = 0;
  if (val < 0)
  {
    minus = 1;
  }
  val = abs(val);
  dig1 = val / 1000;
  val = val % 1000;
  dig2 = val / 100;
  val = val % 100;
  dig3 = val / 10;
  dig4 = val % 10;

  if (dig1 == 0)
  {
    if (dig2 == 0)
    {
      if (dig3 == 0)
      {
        dig1 = 11;
        dig2 = 11;
        if (minus == 1) dig3 = 10;
        else dig3 = 11;
      }
      else
      {
        dig1 = 11;
        if (minus == 1) dig2 = 10;
        else dig2 = 11;
      }
    }
    else
    {
      if (minus == 1) dig1 = 10;
      else dig1 = 11;
    }
  }
  glcddig24(dig1, 1);
  glcddig24(dig2, 2);
  glcddig24(dig3, 3);
  glcddig24(dig4, 4);
}

void glcddig24(byte dig, byte poz)
{

  byte line = 1;
  byte column = 0;
  column = (poz - 1) * 24;
  glcdline(line);
  glcdcolumn(column);
  if (dig <= 10)
  {
    int starti = dig * 168;
    int stopi = (dig + 1) * 168;
    for (int index = starti; index < stopi; index++)
    {
      {
        if (((index - starti) / 24) > line - 1)
        {
          line = line + 1;
          glcdline(line);
          glcdcolumn(column);
        }
        byte payload = pgm(&(DIG24[index]));
        glcddata(payload);
      }
    }
  }
  else
  {
    for (int index = 0; index < 168; index++)
    {
      {
        if ((index / 24) > line - 1)
        {
          line = line + 1;
          glcdline(line);
          glcdcolumn(column);
        }
        glcddata(0x00);
      }
    }
  }
}

void glcdaltbig(int val)
{
  uint8_t dig1 = 0;
  uint8_t dig2 = 0;
  uint8_t dig3 = 0;
  uint8_t minus = 0;
  if (val < 0)
  {
    minus = 1;
  }
  val = abs(val);
  if (val > 999)
  {
  dig1 = val / 1000;
  val = val % 1000;
  dig3 = val / 100;
  dig2 = 10;
  }
  else
  {
    if (val > 99)
    {
      if ( minus == 0)
      {
        dig1 = val / 100;
        val = val % 100;
        dig2 = val / 10;
        dig3 = val % 10;
      }
      else
      {
        dig1 = 11;
        dig2 = 10;
        dig3 = val / 100;
      }
    }
    else
    {
      if (val > 9)
      {
        if ( minus == 0)
        {
          dig1 = 12;
        }
        else
        {
          dig1 = 11;
        }
        dig2 = val / 10;
        dig3 = val % 10;
      }
      else
      {
        dig1 = 12;
        if ( minus == 0)
        {
          dig2 = 12;
        }
        else
        {
          dig2 = 11;
        }
        dig3 = val;
      }
    }
  }
  glcddig32(dig1, 1);
  glcddig32(dig2, 2);
  glcddig32(dig3, 3);
}

void glcddig32(byte dig, byte poz)
{
  byte line = 0;
  byte column = 0;
  column = (poz - 1) * 32;
  glcdline(line);
  glcdcolumn(column);
  if (dig <= 11)
  {
    int starti = dig * 256;
    int stopi = (dig + 1) * 256;
    for (int index = starti; index < stopi; index++)
    {
      {
        if (((index - starti) / 32) > line)
        {
          line = line + 1;
          glcdline(line);
          glcdcolumn(column);
        }
        byte payload = pgm(&(BIG[index]));
        glcddata(payload);
      }
    }
  }
  else
  {
    for (int index = 0; index < 256; index++)
    {
      {
        if ((index / 32) > line)
        {
          line = line + 1;
          glcdline(line);
          glcdcolumn(column);
        }
        glcddata(0x00);
      }
    }
  }
}

