void BMP280begin()
{
  BMP280readCoefficients();
}

uint8_t spi(uint8_t val)
{
  //ledoff();
  SPDR = val;
  nop();                         // wait
  while (!(SPSR & _BV(SPIF)));   // wait
  return SPDR;
}

void BMP280write8(byte reg, byte value)
{
  spion();
  bcsoff();
  spi_master();
  spi(reg & ~0x80); // write, bit 7 low
  spi(value);
  spioff();
  bcson();
}

uint8_t BMP280read8(byte reg)
{
  uint8_t value;
  spion();
  bcsoff();
  spi_master();
  spi(reg | 0x80); // read, bit 7 high
  value = spi(0);
  spioff();
  bcson();
  return value;
}

uint16_t BMP280read16(byte reg)
{
  uint16_t value;
  spion();
  bcsoff();
  spi_master();
  spi(reg | 0x80); // read, bit 7 high
  value = (spi(0) << 8) | spi(0);
  spioff();
  bcson();
  return value;
}


uint16_t BMP280read16_LE(byte reg)
{
  uint16_t temp = BMP280read16(reg);
  return (temp >> 8) | (temp << 8);
}

int16_t BMP280readS16(byte reg)
{
  return (int16_t)BMP280read16(reg);
}

int16_t BMP280readS16_LE(byte reg)
{
  return (int16_t)BMP280read16_LE(reg);
}

uint32_t BMP280read24(byte reg)
{
  uint32_t value;

  spion();
  bcsoff();
  spi_master();
  spi(reg | 0x80); // read, bit 7 high
  value = spi(0);
  value <<= 8;
  value |= spi(0);
  value <<= 8;
  value |= spi(0);
  spioff();
  bcson();
  return value;
}

void BMP280readCoefficients(void)
{
  dig_T1 = BMP280read16_LE(BMP280_REGISTER_DIG_T1);
  dig_T2 = BMP280readS16_LE(BMP280_REGISTER_DIG_T2);
  dig_T3 = BMP280readS16_LE(BMP280_REGISTER_DIG_T3);
  dig_P1 = BMP280read16_LE(BMP280_REGISTER_DIG_P1);
  dig_P2 = BMP280readS16_LE(BMP280_REGISTER_DIG_P2);
  dig_P3 = BMP280readS16_LE(BMP280_REGISTER_DIG_P3);
  dig_P4 = BMP280readS16_LE(BMP280_REGISTER_DIG_P4);
  dig_P5 = BMP280readS16_LE(BMP280_REGISTER_DIG_P5);
  dig_P6 = BMP280readS16_LE(BMP280_REGISTER_DIG_P6);
  dig_P7 = BMP280readS16_LE(BMP280_REGISTER_DIG_P7);
  dig_P8 = BMP280readS16_LE(BMP280_REGISTER_DIG_P8);
  dig_P9 = BMP280readS16_LE(BMP280_REGISTER_DIG_P9);
}

void BMP280Data()
{
  //–ó–∞–ø—Ä–æ—Å –¥–∞–Ω–Ω—ã—Ö
  if (buttwf == 1) //–ü—Ä–∏ –æ–±—Ä–∞–±–æ—Ç–∫–µ –∫–Ω–æ–ø–∫–∏ —Ç—Ä–∞—Ç–∏–º 1 —Ü–∏–∫–ª –Ω–∞ –∑–∞–ø—Ä–æ—Å –≤—Ç–æ—Ä–æ–π –Ω–∞ –ø–æ–ª—É—á–µ–Ω–∏–µ.
  {
    if (altwf == 0)
    {
      BMP280write8(BMP280_REGISTER_CONTROL, 0x32);
      altwf = 1;
      return;
    }
    else
    {
      altwf = 0;
    }
  }
  else
  {
    BMP280write8(BMP280_REGISTER_CONTROL, 0x32);
    //–ü–æ—Å–ø–∏–º –ø–æ–∫–∞ –¥–∞—Ç—á–∏–∫ —Ä–∞–±–æ—Ç–∞–µ—Ç
    swdtxf();
    powerdown();
    wdtss();
  }
  int32_t vart1, vart2;
  int64_t var1, var2, p;

  //–ü–æ–ª—É—á–µ–Ω–∏–µ —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä—ã.
  int32_t adc_T = BMP280read24(BMP280_REGISTER_TEMPDATA);
  adc_T >>= 4;

  vart1  = ((((adc_T >> 3) - ((int32_t)dig_T1 << 1))) *
            ((int32_t)dig_T2)) >> 11;

  vart2  = (((((adc_T >> 4) - ((int32_t)dig_T1)) *
              ((adc_T >> 4) - ((int32_t)dig_T1))) >> 12) *
            ((int32_t)dig_T3)) >> 14;

  int32_t t_fine = vart1 + vart2;

  temperature  = (t_fine * 5 + 128) >> 8;

  //–ü–æ–ª—É—á–µ–Ω–∏–µ –¥–∞–≤–ª–µ–Ω–∏—è.
  int32_t adc_P = BMP280read24(BMP280_REGISTER_PRESSUREDATA);
  adc_P >>= 4;

  var1 = ((int64_t)t_fine) - 128000;
  var2 = var1 * var1 * (int64_t)dig_P6;
  var2 = var2 + ((var1 * (int64_t)dig_P5) << 17);
  var2 = var2 + (((int64_t)dig_P4) << 35);
  var1 = ((var1 * var1 * (int64_t)dig_P3) >> 8) +
         ((var1 * (int64_t)dig_P2) << 12);
  var1 = (((((int64_t)1) << 47) + var1)) * ((int64_t)dig_P1) >> 33;

  if (var1 == 0) {
    pressure = 0;
    return;// avoid exception caused by division by zero
  }
  p = 1048576 - adc_P;
  p = (((p << 31) - var2) * 3125) / var1;
  var1 = (((int64_t)dig_P9) * (p >> 13) * (p >> 13)) >> 25;
  var2 = (((int64_t)dig_P8) * p) >> 19;

  p = ((p + var1 + var2) >> 8) + (((int64_t)dig_P7) << 4);
  pressure = p / 256;
  //pressureint = p / 256;
}

void bmp_qne()
{
  QNEalt = 44330000 * (1.0 - pow(float(pressure) / float(QFE), 0.1902949));
}

void bmp_alt()
{
  OLDalt = QFEalt;
  QFEalt = 44330000 * (1.0 - pow(float(pressure) / float(QFE), 0.1902949));
  DIFalt = QFEalt - OLDalt;
  MeterAlt = QFEalt / 1000;
  if (MeterAlt > lckalt)
  {
    altlf = 1;
    altlcktimer = 0;
    waittimer = 0;
  }
  else
  {
    if (altlcktimer > lcktime) altlf = 0;
  }
}

void bmp_vspeed()
{
  if (buttwf == 0) //–ü—Ä–∏ –æ–±—Ä–∞–±–æ—Ç–∫–µ –∫–Ω–æ–ø–∫–∏ –Ω–µ —Å—á–∏—Ç–∞–µ–º —Å–∫–æ—Ä–æ—Å—Ç—å
  {
    int32_t tmp = DIFalt * 100;
    int32_t tmpt = (wdtt + wdtxft);
    vspeed = tmp / tmpt;
    tmp = abs(vspeed / 100);
    if (tmp > lckspd)
    {
      spdlf = 1;
      spdlcktimer = 0;
      waittimer = 0;
    }
    else
    {
      if (spdlcktimer > lcktime) spdlf = 0;
    }
  }
  else
  {
    vspeed = 0;
    waittimer = 0;
  }
}


void qfeset()
{
  if (spdlf == 0 && altlf == 0 && buttwf == 0)
  {
    QFE = pressure;
    QFEalt = 0;
    //vspeed = 0;
  }
}

void qfeinit()
{
  BMP280Data();
  QFE = pressure;
}
