void portcfg()
{
  DDRD |= (1 << 1);
  DDRD |= (1 << 4);
  DDRB |= (1 << 5);
  DDRB |= (1 << 6);
  DDRB |= (1 << 4);
  DDRB |= (1 << 1);
  DDRB |= (1 << 2);
  cson();
  bcson();
}

void portoff()
{
  csoff();
  bcsoff();
  DDRD &= ~ (1 << 1);
  DDRD &= ~ (1 << 4);
  DDRB &= ~ (1 << 5);
  DDRB &= ~ (1 << 6);
  DDRB &= ~ (1 << 4);
  DDRB &= ~ (1 << 1);
  DDRB &= ~ (1 << 2);
}


void setmstimer()
{
  TCCR3A = 0; //Clear OC3A on compare match
  TCCR3B = 10; //CS31 1Mhz //WGM32 Clear Timer on Compare match
  OCR3A = (0x3E8);  //1000
  TIMSK3 = (1 << 1); //OCIE3A Output Compare A Match Interrupt Enable
}

