void setup()
{
  portcfg();
  ledon();
  nops();
  //minpower();
  setmstimer();
  glcdinit();
  glcdprintln("Load");
  ledoff();
  //dspchk();
  calwdt();
  glcdprint(".");
  BMP280begin();
  glcdprint(".");
  qfeinit();
  glcdprint(".");
  setmstimer();
  glcdline(1);
  glcdprintln(pressure / 100);
  glcdprint(".");
  if (uint32_t(pressure) % 100 < 10) glcdprint("0");
  glcdprint(uint32_t(pressure) % 100);
  glcdprint(" hPa  ");
  wait(6000);
  buttintcfg(0); //rising 0 falling 1 change 2 low 3
  glcdclear();
  astate = 1;
  minpower();
  ms = 0;
}


void wait(uint16_t val)
{
  ms = 0;
  while (val > ms);
}

void nops()
{
  uint32_t i = 8000000;
  while (i > 0)
  {
    i--;
    nop();
  }
}

void loop()
{
  switch (astate)
  {
    case 0:
      lckspd = 1;
      lckalt = 50;
      lcktime = 30000;
      pwroff();
      break;
    case 1:
      lckspd = 1;
      lckalt = 50;
      lcktime = 30000;
      pwrsave();
      break;
    case 2:
      lckspd = 1;
      lckalt = 50;
      lcktime = 30000;
      idle();
      break;
    case 3:
      lckspd = 2;
      lckalt = 300;
      lcktime = 60000;
      active();
      break;
    case 4:
      lckspd = 3;
      lckalt = 300;
      lcktime = 180000;
      altimeter();
      break;
    case 5:
      lckspd = 2;
      lckalt = 300;
      lcktime = 60000;
      menu();
      break;
  }
}

void wdtss()
{
  switch (astate)
  {
    case 0:
      wdtoff();
      break;
    case 1:
      swdtxs();
      break;
    case 2:
      swdts();
      break;
    case 3:
      swdtn();
      break;
    case 4:
      swdtf();
      break;
    case 5:
      swdtn();
      break;
  }
}

void pwroff()
{
  wdtoff();
  wdtf = 0;
  glcdstop();
  portoff();

  //glcdclear();
  //glcdprintln("Off");

  uint8_t b = 1;
  while (b)
  {
    powerdown();
    if (wdtf == 1)
    {
      buttwrk();
    }
    buttfp();
    if (buttdpf == 1)
    {
      buttdpf = 0;
      b = 0;
      astate = 2;
    }
  }
  portcfg();
  ms = 0;
  wrktimer = 0;
  waittimer = 0;
  spdlcktimer = 0; // —Ç–∞–π–º–µ—Ä –±–ª–æ–∫–∏—Ä–æ–≤–∫–∏ –ø–æ —Å–∫–æ—Ä–æ—Å—Ç–∏
  altlcktimer = 0; // —Ç–∞–π–º–µ—Ä –±–ª–æ–∫–∏—Ä–æ–≤–∫–∏ –ø–æ –≤—ã—Å–æ—Ç–µ
  glcdinit();
  qfeinit();
}

void pwrsave()
{
  swdtxs();
  wdtf = 0;
  glcdstop();

  //glcdclear();
  //glcdprintln("PWRSave");

  uint8_t b = 1;
  while (b)
  {
    if (wdtf == 1)
    {
      BMP280Data();
      bmp_alt();
      bmp_vspeed();
      buttwrk();
      timers();
      wdtf = 0;
      if (buttdpf == 1 || abs(DIFalt / 1000) > 2)
      {
        buttdpf = 0;
        b = 0;
        astate = 2;
        spdlf = 1;
		spdlcktimer = 0;
        waittimer = 0;
        return;
      }
      else
      {
        qfeset();
      }
    }
    //screen();
    //if (wrktimer > 57600000 && spdlf == 0 && altlf == 0)
    //{
    //  b = 0;
    //  astate = 0;
    //  return;
    //}
    if (butthf == 1 && buttwf == 0)
    {
      butthf = 0;
      b = 0;
      astate = 5;
      return;
    }
    powerdown();
    buttfp();
  }
  glcdinit();
}

void idle()
{
  swdts();
  glcdinit();
  glcdclear();
  BMP280Data();
  uint8_t b = 1;
  while (b)
  {
    if (wdtf == 1)
    {
      BMP280Data();
      bmp_alt();
      bmp_vspeed();
      buttwrk();
      timers();
      wdtf = 0;
      if (buttdpf == 1 || abs(DIFalt / 1000) > 2)
      {
        buttdpf = 0;
        b = 0;
        astate = 3;
        spdlf = 1;
		spdlcktimer = 0;
        waittimer = 0;
        return;
      }
      else
      {
        qfeset();
      }
    }
    screen();
    buttfp();
    if (waittimer > 1200000 && spdlf == 0 && altlf == 0)
    {
      b = 0;
      astate = 1;
      return;
    }
    if (butthf == 1 && buttwf == 0)
    {
      butthf = 0;
      b = 0;
      astate = 5;
      return;
    }
    powerdown();
  }
}

void active()
{
  swdtn();
  glcdclear();
  uint8_t b = 1;
  while (b)
  {
    if (wdtf == 1)
    {
      BMP280Data();
      bmp_alt();
      bmp_vspeed();
      qfeset();
      buttwrk();
      timers();
      wdtf = 0;
    }
    screen();
    buttfp();
    //buttscreen();
    powerdown();
    if (abs(vspeed / 100) > 10 || MeterAlt > 300)
    {
      buttdpf = 0;
      b = 0;
      astate = 4;
    }
    if (waittimer > 600000 && spdlf == 0 && altlf == 0)
    {
      b = 0;
      astate = 2;
    }
    if (butthf == 1 && buttwf == 0)
    {
      butthf = 0;
      b = 0;
      astate = 5;
      return;
    }
  }
}

void altimeter()
{
  swdtf();
  //glcdclear();
  sstate = 0;
  uint8_t b = 1;
  while (b)
  {
    if (wdtf == 1)
    {
      BMP280Data();
      bmp_alt();
      bmp_vspeed();
      timers();
      wdtf = 0;
      buttwrk();
    }
    if (spdlf == 0 && altlf == 0)
    {
      b = 0;
      astate = 3;
      return;
    }
    if (butthf == 1 && buttwf == 0)
    {
      butthf = 0;
      b = 0;
      astate = 5;
      return;
    }
    buttfp();
    screen();
    powerdown();
  }
}

