void buttfp()
{
  if (buttf == 1 && buttwf == 0) //–ö–Ω–æ–ø–∫–∞ –Ω–∞–∂–∞—Ç–∞ –≤–ø–µ—Ä–≤—ã–µ
  {
    swdtxf();   //–£—Å–∫–æ—Ä–∏–º—Å—è
    buttwf = 1; //–£—Å—Ç–∞–Ω–æ–≤–∏–º —Ñ–ª–∞–≥ —Ä–∞–±–æ—Ç—ã –Ω–∞–¥ –∫–Ω–æ–ø–∫–æ–π
    buttlhf = 0;
    butthf = 0;
    buttpf = 0;
    buttdpf = 0;
    buttf = 0;
    buttpt = 0; //–û–±–Ω—É–ª–∏–º —Å—á—ë—Ç—á–∏–∫
    buttrt = 0;
    buttwt = 0;
  }
}

void buttwrk()
{
  if (buttwf == 1) // –ò–¥—ë—Ç –æ–±—Ä–∞–±–æ—Ç–∫–∞ –Ω–∞–∂–∞—Ç–∏–π
  {
    if (buttwt > 30) //–ó–∞–¥–µ—Ä–∂–∫–∞ –Ω–∞ –¥—Ä–µ–±–µ–∑–≥
    {
      if (buttstat())
      { //–∫–Ω–æ–ø–∫–∞ –µ—â—ë –Ω–∞–∂–∞—Ç–∞
        if (buttpt > 400 && buttpt < 2000)
        {
          butthf = 1;
        }
        if (buttpt > 2000 && buttlhf == 0)
        {
          buttlhf = 1;
          butthf = 0;
          ledswb();
          buttwf = 0;
        }
        buttpt += wdtt;
      }
      else
      { //–∫–Ω–æ–ø–∫–∞ —É–∂–µ –æ—Ç–ø—É—â–µ–Ω–∞
        if (buttrt > 30) //
        {
          buttintcfg(3);
          if (buttdpf == 1 || butthf == 1 || buttlhf == 1)
          {
            buttwf = 0;
            wdtss();
          }
        }
        if (buttrt < 200 && buttpt < 400 && buttf == 1 )
        {
          buttdpf = 1;
          buttrt = 0;
          buttf = 0;
          buttwf = 0;
        }
        if (buttrt > 200 && (buttlhf + butthf) != 1) //
        {
          buttpf = 1;
          buttwf = 0;
          wdtss();
        }
        buttrt += wdtt;
      }
    }
    buttwt += wdtt;
  }
}


void menu()
{
  swdtn();
  glcdclear();
  glcdline(0);
  glcdprintln(">MENU<");
  menupoz = 1;
  uint8_t b = 1;
  while (b)
  {
    if (wdtf == 1)
    {
      BMP280Data();
      bmp_alt();
      bmp_vspeed();
      timers();
      wdtf = 0;
      buttwrk();
    }
    if (butthf == 1 && buttwf == 0)
    {
      action();
      butthf = 0;
      b = 0;
      menupoz = 0;
      return;
    }
    buttfp();
    glcdline(0);
    glcdprintln(">MENU<");
    glcdline(1);
    glcdprintln(" EXIT");
    glcdline(2);
    glcdprintln(" DIAG");
    glcdline(3);
    glcdprintln(" ALTIMETER");
    glcdline(4);
    glcdprintln(" ZERO");
    glcdline(5);
    glcdprintln(" OFF");
    if (buttpf == 1 && menupoz < 5)
    {
      menupoz++;
      buttpf = 0;
    }
    if (buttdpf == 1 && menupoz > 1)
    {
      menupoz--;
      buttdpf = 0;
    }
    glcdline(menupoz);
    if (butthf == 1)
    {
      glcdprintln(">");
    }
    else
    {
      glcdprintln("-");
    }
    if (waittimer > 60000)
    {
      b = 0;
      astate = 3;
      return;
    }
    powerdown();
  }
}


void action()
{
  switch (menupoz)
  {
    case 1:
      astate = 2;
      break;
    case 2:
      astate = 3;
      sstate = 1;
      break;
    case 3:
      astate = 4;
      sstate = 0;
      break;
    case 4:
      qfeinit();
      astate = 1;
      break;
    case 5:
      astate = 0;
      break;
  }
}

void screen()
{
  switch (sstate)
  {
    case 0:
      glcdaltbig(MeterAlt);
      break;
    case 1:
      diagscreen();
      break;
  }
}
